import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { watchAPI } from '../services/api';
import Loading from './Common/Loading';
import WatchCard from './Buyer/WatchCard';

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 2rem;
`;

const Title = styled.h1`
  color: #2c3e50;
  margin: 0;
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 1.5rem;
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 3rem 1rem;
  color: #7f8c8d;
`;

const Home = () => {
  const [watches, setWatches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [errored, setErrored] = useState(false);

  useEffect(() => {
    const fetchWatches = async () => {
      try {
        const response = await watchAPI.getAll();
        setWatches(response.data || []);
      } catch (e) {
        setErrored(true);
        // Fallback demo data so homepage isn't blank when API is down
        setWatches([
          { id: 1001, name: 'Submariner Date', brand: 'Rolex', price: 12999.99, img: 'https://images.pexels.com/photos/190819/pexels-photo-190819.jpeg', existing_status: true, seller_id: 0 },
          { id: 1002, name: 'Speedmaster Pro', brand: 'Omega', price: 5999.0, img: 'https://images.pexels.com/photos/190819/pexels-photo-190819.jpeg', existing_status: true, seller_id: 0 },
          { id: 1003, name: 'Nautilus', brand: 'Patek Philippe', price: 74999.0, img: 'https://images.pexels.com/photos/190819/pexels-photo-190819.jpeg', existing_status: true, seller_id: 0 },
        ]);
      } finally {
        setLoading(false);
      }
    };
    fetchWatches();
  }, []);

  if (loading) return <Loading />;

  return (
    <Container>
      <Header>
        <Title>Explore Vintage Watches</Title>
      </Header>

      {watches.length === 0 ? (
        <EmptyState>
          {errored ? 'Could not load watches from the server. Showing nothing for now.' : 'No watches available yet.'}
        </EmptyState>
      ) : (
        <Grid>
          {watches.map(watch => (
            <WatchCard key={watch.id} watch={watch} onPurchase={() => {}} />
          ))}
        </Grid>
      )}
    </Container>
  );
};

export default Home;
